mapper.select:
select count(1)
from mall_user
where username = #{username}

select count(1)
from mall_user
where email = #{email}

select count(1)
from mall_user
where email = #{email}
  and id != #{id}

select count(1)
from mall_user
where phone = #{phone}

select count(1)
from mall_user
where phone = #{phone}
  and id != #{id}

select question
from mall_user
where username = #{username}

select count(1)
from mall_user
where username = #{username}
  and question = #{question}
  and answer = #{answer}

mapper.update:
update mall_user
set password = #{password}
where username = #{username}

mapper.select:
select count(1)
from mall_user
where id = #{id}
  and password = #{password}

mapper.update:
update mall_user
set password = #{password}
where id = #{id}

mapper.select:
select id, username, email, phone, role, question, create_time createTime, update_time updateTime
from mall_user
where id = #{id}
